import React, { useState, useEffect } from 'react';
import { handleSuccess, handleError } from '../utils.js';
import { Link, useNavigate } from 'react-router-dom';
import { ToastContainer } from 'react-toastify';
import './LogIn.css';

function LogIn({ onLoginSuccess }) {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  // Google login handler
  const loginWithGoogle = () => {
    window.open("http://localhost:8080/auth/google", "_self");
  };

  // Fetch authenticated user details after Google sign-in
  const fetchUserDetails = async () => {
    try {
      const response = await fetch('http://localhost:8080/auth/user', {
        method: 'GET',
        credentials: 'include', // Include session cookies
      });

      if (response.ok) {
        const data = await response.json();
        const { token, username } = data;

        // Store details in localStorage
        localStorage.setItem("token", token);
        localStorage.setItem("loggedInUser", username);
        localStorage.setItem("isLoggedIn", true);

        handleSuccess('Google login successful!');
        onLoginSuccess();
        navigate('/'); // Redirect to home page
      } else {
        setError('Failed to fetch user details. Please try again.');
      }
    } catch (err) {
      console.error('Error fetching user details:', err);
      setError('An error occurred. Please try again later.');
    }
  };

  // Trigger user data fetch if redirected from Google
  useEffect(() => {
    fetchUserDetails();
  }, []);

  // Form submission handler
  const handleSubmit = async (event) => {
    event.preventDefault();
    setError(''); // Clear previous errors

    try {
      const response = await fetch('http://localhost:8080/auth/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, password }),
      });

      const data = await response.json();
      if (response.ok && data.success) {
        handleSuccess(data.message);
        localStorage.setItem("token", data.token);
        localStorage.setItem("loggedInUser", data.username);
        localStorage.setItem("isLoggedIn", true);

        // Notify parent component and navigate
        onLoginSuccess();
        setTimeout(() => navigate('/'), 1000);
      } else {
        setError(data.error || 'Invalid credentials');
      }
    } catch (error) {
      console.error('Error:', error);
      setError('An error occurred. Please try again later.');
    }
  };

  return (
    <div className="login-page">
      <div className="login-container">
        <h1 className="login-heading">Log In</h1>
        {error && <p className="error-text" style={{ color: 'red' }}>{error}</p>}

        <form className="form-container" onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="email" className="labels">Email</label>
            <input
              type="email"
              id="email"
              className="input"
              placeholder="Enter your email"
              required
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              autoFocus
            />
          </div>
          <div className="form-group">
            <label htmlFor="password" className="labels">Password</label>
            <input
              type="password"
              id="password"
              className="input"
              placeholder="Enter your password"
              required
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              onBlur={(e) => {
                if (!e.target.value) setError('Password is required');
              }}
            />
          </div>
          <div className="remember-forgot-container">
            <Link to="/forgot-password" className="forgot">Forgot Password?</Link>
          </div>
          <button type="submit" className="log-in-btn">Log In</button>

          <div className="social-container">
            <button type="button" className="social-btn" onClick={loginWithGoogle}>
              <img
                src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcS6WwgH7Nl5_AW9nDCnR2Ozb_AU3rkIbSJdAg&s"
                alt="Google"
                className="social-icon"
              />
              Google
            </button>
            <button type="button" className="social-btn">
              <img
                src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQiXN9xSEe8unzPBEQOeAKXd9Q55efGHGB9BA&s"
                alt="Facebook"
                className="social-icon"
              />
              Facebook
            </button>
          </div>

          <div className="text-center">
            <Link to="/signup" className="link-button">Don't have an account? Sign up</Link>
          </div>
        </form>
        <ToastContainer />
      </div>
    </div>
  );
}

export default LogIn;
