import React, { useState } from 'react';
import axios from 'axios';
import './Api.css';

function Api() {
  const [file, setFile] = useState(null);
  const [result, setResult] = useState(null);
  const [errorMessage, setErrorMessage] = useState(''); // State for handling errors

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
    setErrorMessage(''); // Clear any existing error messages when file changes
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!file) {
      setErrorMessage('Please select an image file before uploading.');
      return;
    }

    const formData = new FormData();
    formData.append('image', file);

    try {
      const response = await axios.post('http://127.0.0.1:5000/api/predict', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      // Save the result to the database (only if logged in)
      const predictionData = response.data;
      const token = localStorage.getItem('token'); // Get the token from localStorage

      if (token) {
        // Proceed to save the diagnosis history if logged in
        await axios.post(
          'http://localhost:8080/upload-and-diagnose',
          {
            diseaseName: predictionData.title,
            imageUrl: predictionData.image_url,
            token, // Send the token in the request body
          },
          {
            withCredentials: true, // Ensure the session cookie is included (if necessary)
          }
        );
      }

      setResult(predictionData);
      setErrorMessage(''); // Clear errors on success
    } catch (error) {
      if (error.response && error.response.data && error.response.data.error) {
        setErrorMessage(error.response.data.error); // Display backend error message
      } else {
        setErrorMessage('An unexpected error occurred. Please try again.');
      }
      setResult(null); // Clear the result if an error occurs
    }
  };

  return (
    <div className="main">
      <h1 className="main-heading">Plant Disease Detector</h1>

      <form onSubmit={handleSubmit} className="form">
        <input type="file" className="choose" onChange={handleFileChange} />
        <br />
        <br />
        <button type="submit" className="upload">
          Upload
        </button>
      </form>

      {/* Display error message if any */}
      {errorMessage && <p className="error-message">{errorMessage}</p>}

      {/* Display prediction result if available */}
      {result && (
        <div className="container result">
          <div className="row">
            <h2 className="prediction-result col col-12">Prediction Result</h2>

            <div className="col-6 div">
              <p className="disease">Disease:</p>
              <p className="disease-des">{result.title}</p>
            </div>
            <div className="col-6 image-div">
              <img className="image" src={result.image_url} alt="Disease" />
            </div>
            <div className="col-12 div">
              <p className="disease">Description:</p>
              <p className="disease-des">{result.description}</p>
            </div>
            <div className="col-12 div">
              <p className="disease">Prevention:</p>
              <p className="disease-des">{result.prevent}</p>
            </div>

            <div className="col-12 div">
              <h3 className="supplement disease">Supplement Information</h3>
              <p className="disease-des">
                <strong>Supplement:</strong> {result.supplement_name}
              </p>
            </div>
            <div className="col-12 div">
              <img
                className="supplement-image"
                src={result.supplement_image_url}
                alt="Supplement"
              />
              <a href={result.supplement_buy_link} target="_blank" rel="noopener noreferrer">
                Buy Supplement
              </a>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default Api;
