import React, { useState } from 'react';
import { handleSuccess, handleError } from '../utils.js'; // Make sure utils has necessary functions
import { Link, useNavigate } from 'react-router-dom';
import { ToastContainer } from 'react-toastify';
import './LogIn.css';

function LogIn({ onLoginSuccess }) {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const loginWithGoogle = () => {
    window.open("http://localhost:8080/auth/google", "_self");
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    const formData = { email, password };

    try {
      const response = await fetch('http://localhost:8080/auth/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData),
      });

      const data = await response.json();
      const { success, message, token, username, error } = data;

      if (response.ok && success && token) {
        handleSuccess(message);
        localStorage.setItem("token", token);
        localStorage.setItem("loggedInUser", username);
        
        onLoginSuccess();
        
        // Delay to show success message before navigation
        setTimeout(() => navigate('/'), 1000);
      } else {
        setError(error || 'Invalid credentials');
      }
    } catch (error) {
      console.error('Login failed:', error);
      setError('An error occurred. Please try again later.');
    }
  };

  return (
    <div className="login-page">
      <div className="login-container">
        <h1 className="login-heading">Log In</h1>
        {error && <p className="error-text" style={{ color: 'red' }}>{error}</p>}
        
        <form className="form-container" onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="email" className="labels">Email</label>
            <input
              type="email"
              id="email"
              className="input"
              placeholder="Enter your email"
              required
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              autoFocus
            />
          </div>
          <div className="form-group">
            <label htmlFor="password" className="labels">Password</label>
            <input
              type="password"
              id="password"
              className="input"
              placeholder="Enter your password"
              required
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              onBlur={(e) => {
                if (!e.target.value) setError('Password is required');
              }}
            />
          </div>

          <div className="remember-forgot-container">
            <Link to="/forgot-password" className="forgot">Forgot Password?</Link>
          </div>

          <button type="submit" className="log-in-btn">Log In</button>

          <div className="social-container">
            <button type="button" className="social-btn" onClick={loginWithGoogle}>
              <img
                src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcS6WwgH7Nl5_AW9nDCnR2Ozb_AU3rkIbSJdAg&s"
                alt="Google"
                className="social-icon"
              />
              Log in with Google
            </button>
          </div>

          <div className="text-center">
            <Link to="/signup" className="link-button">Don't have an account? Sign up</Link>
          </div>
        </form>

        <ToastContainer />
      </div>
    </div>
  );
}

export default LogIn;
