import React, { useState , useEffect } from 'react';
import { handleSuccess, handleError } from '../utils.js';
import { Link, useNavigate } from 'react-router-dom';
import { ToastContainer } from 'react-toastify';
import './SignUp.css';

function SignUp() {
  const [role, setRole] = useState('');
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [apiError, setApiError] = useState(''); // Unified state for error messages

  const navigate = useNavigate();

  const loginWithGoogle = () => {
    window.open("http://localhost:8080/auth/google", "_self");
  };
  useEffect(() => {
    // Assuming you're handling the token fetching after the redirect back to your frontend
    const fetchGoogleToken = async () => {
      try {
        const response = await fetch('http://localhost:8080/auth/google/callback', {
          method: 'GET',
          credentials: 'include', // Use this if your server uses cookies for session management
        });
        
        const data = await response.json();
  
        if (response.ok && data.success) {
          // Store token and username in localStorage or state
          localStorage.setItem('token', data.token);
          localStorage.setItem('username', data.username);
          localStorage.setItem('isLoggedIn', true);
  
          // Navigate to a different page
          navigate('/dashboard');
        } else {
          console.error('Google login failed');
        }
      } catch (error) {
        console.error('Error fetching token:', error);
      }
    };
  
    fetchGoogleToken();
  }, [navigate]);
  // Form submit handler
  const handleSubmit = async (event) => {
    event.preventDefault();
    if (!username.trim()) return setApiError('Username is required');
    if (!role) return setApiError('Please select a role');

    const formData = { username, email, password, role };

    try {
      const response = await fetch('http://localhost:8080/auth/signup', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData),
      });

      const data = await response.json();
      console.log(data);
      const { success, message, error ,token} = data;

      if (response.ok && success && token) {
        handleSuccess(message);
        localStorage.setItem("token", token);
        localStorage.setItem("loggedInUser", username);
        localStorage.setItem("isLoggedIn", true);
        setTimeout(() => navigate('/login'), 2000); // Redirect after a success message
      } else {
        setApiError(error || 'Signup failed, please try again.');
      }
    } catch (error) {
      console.error('Signup error:', error);
      setApiError('An error occurred. Please try again later.');
    }
  };
  

  return (
    <div className="signup-page">
      <div className="signup-container">
        <h1 className="sign-up-heading">Get Started</h1>
        <form className="form-container" onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="name" className="signup-label">Username</label>
            <input
              type="text"
              id="name"
              className="signup-input"
              placeholder="Enter your username"
              required
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              autoFocus
            />
          </div>
          <div className="form-group">
            <label htmlFor="email" className="signup-label">Email Address</label>
            <input
              type="email"
              id="email"
              className="signup-input"
              placeholder="Enter your email"
              required
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
          </div>
          <div className="form-group">
            <label htmlFor="password" className="signup-label">Password</label>
            <input
              type="password"
              id="password"
              className="signup-input"
              placeholder="Enter your password"
              required
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </div>
          <div className="form-group">
            <label htmlFor="option" className="signup-label">Select your role</label>
            <select
              id="option"
              className="signup-select"
              value={role}
              onChange={(e) => setRole(e.target.value)}
            >
              <option value="" disabled>Select a role</option>
              <option value="Agro Scientist">Agro Scientist</option>
              <option value="Farmer">Farmer</option>
              <option value="Student">Student</option>
              <option value="Others">Others</option>
            </select>
          </div>

          {/* Display the error message if it exists */}
          {apiError && <p className="error-text" style={{ color: 'red' }}>{apiError}</p>}

          <button type="submit" className="signup-btn">Sign Up</button>
        </form>

        <ToastContainer />
        <p className="or-text">or</p>

        <div className="social-buttons">
          <button className="social-btn" onClick={loginWithGoogle}>
            <img
              src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcS6WwgH7Nl5_AW9nDCnR2Ozb_AU3rkIbSJdAg&s"
              alt="Google"
              className="social-icon"
            />
            Google
          </button>
          <button className="social-btn">
            <img
              src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQiXN9xSEe8unzPBEQOeAKXd9Q55efGHGB9BA&s"
              alt="Facebook"
              className="social-icon"
            />
            Facebook
          </button>
        </div>

        <div className="text-center">
          <Link to="/login" className="link-button">Have an account? Log in</Link>
        </div>
      </div>
    </div>
  );
}

export default SignUp;
