import React, { useState } from 'react';
import { handleSuccess } from '../utils.js';
import { Link, useNavigate } from 'react-router-dom';
import { ToastContainer } from 'react-toastify';
import './LogIn.css';

function LogIn() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [emailError, setEmailError] = useState('');
  const [passwordError, setPasswordError] = useState('');
  const [generalError, setGeneralError] = useState('');
  const navigate = useNavigate();

  const loginWithGoogle = () => {
    window.open('http://localhost:8080/auth/google', '_self');
  };

  const validateInputs = () => {
    let isValid = true;

    if (!email || !/^\S+@\S+\.\S+$/.test(email)) {
      setEmailError('Please enter a valid email address.');
      isValid = false;
    } else {
      setEmailError('');
    }

    if (!password || password.length < 6) {
      setPasswordError('Password must be at least 6 characters long.');
      isValid = false;
    } else {
      setPasswordError('');
    }

    return isValid;
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    if (!validateInputs()) return;

    const formData = { email, password };

    try {
      const response = await fetch('http://localhost:8080/auth/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      const data = await response.json();
      const { success, message, token, username } = data;

      if (response.ok && success && token) {
        handleSuccess(message);
        localStorage.setItem('token', token);
        localStorage.setItem('LoggedInUser', username);
        localStorage.setItem('isLoggedIn', true);

        setTimeout(() => {
          navigate('/'); // Redirect after successful login
        }, 1000);
      } else {
        if (data.error === 'Invalid email') {
          setEmailError('Invalid email.');
        } else if (data.error === 'Invalid password') {
          setPasswordError('Invalid password.');
        } else {
          setGeneralError(data.error || 'Invalid credentials. Please try again.');
        }
      }
    } catch (error) {
      console.error('Error:', error);
      setGeneralError('An error occurred. Please try again later.');
    }
  };

  return (
    <div className="login-page">
      <div className="login-container">
        <form className="form" onSubmit={handleSubmit}>
          <h2>Log In</h2>
          {generalError && <p className="error-text general-error">{generalError}</p>}

          <div className="form-group">
            <label>Email</label>
            <input
              placeholder="Enter your Email"
              className={`input ${emailError ? 'input-error' : ''}`}
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
            {emailError && <p className="error-text">{emailError}</p>}
          </div>

          <div className="form-group">
            <label>Password</label>
            <input
              placeholder="Enter your Password"
              className={`input ${passwordError ? 'input-error' : ''}`}
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
            {passwordError && <p className="error-text">{passwordError}</p>}
          </div>

          <div className="flex-row">
            <span className="span">
              <Link to="/forgot-password">Forgot password?</Link>
            </span>
          </div>

          <button type="submit" className="button-submit">
            Log In
          </button>

          <p className="p">
            Don't have an account?{' '}
            <Link to="/signup">
              <span className="span">Sign Up</span>
            </Link>
          </p>

          <p className="p line">Or With</p>

          <div className="flex-row">
            <button className="btn google" onClick={loginWithGoogle}>
              <svg
                xmlns="http://www.w3.org/2000/svg"
                width="20"
                height="20"
                viewBox="0 0 512 512"
              >
                <path
                  d="M113.47,309.408L95.648,375.94l-65.139,1.378C11.042,341.211,0,299.9,0,256 c0-42.451,10.324-82.483,28.624-117.732h0.014l57.992,10.632l25.404,57.644c-5.317,15.501-8.215,32.141-8.215,49.456 C103.821,274.792,107.225,292.797,113.47,309.408z"
                  style={{ fill: '#FBBB00' }}
                />
                <path
                  d="M507.527,208.176C510.467,223.662,512,239.655,512,256c0,18.328-1.927,36.206-5.598,53.451 c-12.462,58.683-45.025,109.925-90.134,146.187l-0.014-0.014l-73.044-3.727l-10.338-64.535 c29.932-17.554,53.324-45.025,65.646-77.911h-136.89V208.176h138.887L507.527,208.176L507.527,208.176z"
                  style={{ fill: '#518EF8' }}
                />
                <path
                  d="M416.253,455.624l0.014,0.014C372.396,490.901,316.666,512,256,512 c-97.491,0-182.252-54.491-225.491-134.681l82.961-67.91c21.619,57.698,77.278,98.771,142.53,98.771 c28.047,0,54.323-7.582,76.87-20.818L416.253,455.624z"
                  style={{ fill: '#28B446' }}
                />
                <path
                  d="M419.404,58.936l-82.933,67.896c-23.335-14.586-50.919-23.012-80.471-23.012 c-66.729,0-123.429,43.243-143.957,102.979l-83.615-68.241C74.141,54.852,159.383,0,256,0 C318.115,0,375.056,22.126,419.404,58.936z"
                  style={{ fill: '#F14336' }}
                />
              </svg>
              Sign in with Google
            </button>
          </div>
        </form>
        <ToastContainer />
      </div>
    </div>
  );
}

export default LogIn;
