import { useState, useEffect, createContext } from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';
import axios from 'axios';
import SignUp from './Components/SignUp.jsx';
import LogIn from './Components/LogIn.jsx';
import Home from './Components/Home.jsx';
import Profile from './Components/Profile.jsx';
import Error from './Components/Error.jsx';
import UploadAndDiagnosis from './Components/UploadAndDiagnosis.jsx';
import ForgotPassword from './Components/ForgotPassword.jsx';
import ResetPassword from './Components/ResetPassword.jsx';
import OTPVerification from './Components/OTPVerification.jsx';

// Context for authentication
export const AuthContext = createContext();

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [loading, setLoading] = useState(true);

  // Function to handle login success and store token
  const handleLoginSuccess = (token) => {
    localStorage.setItem('token', token);  // Save token in localStorage
    setIsLoggedIn(true);                   // Update login state
  };

  useEffect(() => {
    const token = localStorage.getItem('token');

    if (token) {
      // Validate token on initial load
      axios.get('http://localhost:8080/user', {
        headers: { Authorization: `Bearer ${token}` },
      })
      .then((res) => {
        if (res.data.user) {
          setIsLoggedIn(true);
        } else {
          setIsLoggedIn(false);
          localStorage.removeItem('token'); // Remove token if validation fails
        }
      })
      .catch(() => {
        setIsLoggedIn(false);
        localStorage.removeItem('token');
      })
      .finally(() => setLoading(false));
    } else {
      setLoading(false); // No token implies user is logged out
    }
  }, []);

  // Logout function
  const handleLogout = async () => {
    try {
      const token = localStorage.getItem('token');
      await axios.get('http://localhost:8080/logout', {
        headers: { Authorization: `Bearer ${token}` },
      });
      setIsLoggedIn(false);
      localStorage.removeItem('token');
    } catch (err) {
      console.log('Error logging out:', err);
    }
  };

  if (loading) return <div>Loading...</div>; // Optional: Replace with a loading spinner

  return (
    <AuthContext.Provider value={{ isLoggedIn, handleLoginSuccess, handleLogout }}>
      <Routes>
        <Route path="/" element={<Navigate to="/home" />} />
        <Route path="/home" element={<Home />} />
        <Route path="/login" element={isLoggedIn ? <Navigate to="/" /> : <LogIn onLoginSuccess={handleLoginSuccess} />} />
        <Route path="/UploadAndDiagnosis" element={<UploadAndDiagnosis />} />
        <Route path="/signup" element={isLoggedIn ? <Navigate to="/" /> : <SignUp />} />
        <Route path="/forgot-password" element={isLoggedIn ? <Navigate to="/" /> : <ForgotPassword />} />
        <Route path="/verify-otp" element={isLoggedIn ? <Navigate to="/" /> : <OTPVerification />} />
        <Route path="/reset-password" element={isLoggedIn ? <Navigate to="/" /> : <ResetPassword />} />
        <Route path="/profile" element={isLoggedIn ? <Profile /> : <Navigate to="/login" />} />
        <Route path="*" element={<Error />} />
      </Routes>
    </AuthContext.Provider>
  );
}

export default App;
