import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const OAuthHandler = () => {
  const navigate = useNavigate();

  useEffect(() => {
    const fetchToken = async () => {
      try {
        const response = await fetch('http://localhost:8080/auth/token', {
          method: 'POST',
          credentials: 'include', // Ensure cookies are sent for authentication
          headers: {
            'Content-Type': 'application/json',
          },
        });

        if (!response.ok) {
          throw new Error('Failed to fetch token');
        }

        const { token, username, email } = await response.json();

        // Store the token and user data securely in localStorage
        localStorage.setItem('token', token);
        localStorage.setItem('user', JSON.stringify({ username, email }));
        localStorage.setItem('isLoggedIn', true);

        // Navigate to the dashboard or home page
        navigate('/');
      } catch (error) {
        console.error('Error fetching token:', error.message);
        navigate('/'); // Redirect to login if token fetching fails
      }
    };

    fetchToken();
  }, [navigate]);

  return <div>Loading...</div>; // Show a loading indicator while fetching the token
};

export default OAuthHandler;
