import React, { useState } from "react";

const FeedbackForm = () => {
  // State to store form data
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    message: "",
  });

  // State for handling success/error messages
  const [statusMessage, setStatusMessage] = useState("");

  // Handle form field changes
  const handleInputChange = (e) => {
    const { id, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [id]: value,
    }));
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    // Basic client-side validation
    if (!formData.name || !formData.email || !formData.message) {
      setStatusMessage("All fields are required.");
      return;
    }

    try {
      // Send form data to the backend
      const response = await fetch("http://localhost:8080/submit-feedback", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(formData),
      });

      const result = await response.json();

      if (response.status === 200) {
        setStatusMessage("Feedback submitted successfully");
        setFormData({
          name: "",
          email: "",
          message: "",
        });
      } else {
        setStatusMessage(result.message || "Failed to submit feedback");
      }
    } catch (error) {
      setStatusMessage("An error occurred, please try again later.");
      //console.error("Error submitting feedback:", error);
    }
  };

  return (
    <section id = "contact-container" className="contact-container">
      <div className="contact-card">
        <form className="form-section" onSubmit={handleSubmit}>
          <h2 className="h2">Contact Us</h2>
          {["Name", "Email", "Message"].map((field, idx) => (
            <div className="form-group" key={idx}>
              <label className = "label" htmlFor={field.toLowerCase()}>{field}</label>
              {field === "Message" ? (
                <textarea
                
                  id="message"
                  rows="5"
                  placeholder={`Your ${field}`}
                  value={formData.message}
                  onChange={handleInputChange}
                  required
                />
              ) : (
                <input
                  type={field.toLowerCase()}
                  id={field.toLowerCase()}
                  placeholder={`Your ${field}`}
                  value={formData[field.toLowerCase()]}
                  onChange={handleInputChange}
                  required
                />
              )}
            </div>
          ))}
          <button type="submit" className="button">
            Send Message
          </button>
          {statusMessage && <p>{statusMessage}</p>}
        </form>
        <div className="image-section">
          <img
            src="https://www.digitalsilk.com/wp-content/uploads/2023/11/contact-us-page-designs-hero.jpg"
            alt="Contact Illustration"
          />
        </div>
      </div>
    </section>
  );
};

export default FeedbackForm;
