import React, { useEffect, useState } from 'react';
import { Navbar, Nav, Container, NavDropdown } from 'react-bootstrap';
import { LinkContainer } from 'react-router-bootstrap';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';

function MyNavbar() {
  const [userData, setUserData] = useState(null);
  const navigate = useNavigate();

  // Function to fetch the user's data using a token
  const getUser = async () => {
    try {
      // Retrieve the token from localStorage
      const token = localStorage.getItem('token');
      if (token) {
        const response = await axios.get('http://localhost:8080/login/success', {
          headers: { Authorization: `Bearer ${token}` }, // Set the Authorization header with the token
          withCredentials: true,
        });
        // If user data is retrieved, set it in state and localStorage
        if (response.data.user) {
          const { username } = response.data.user;
          setUserData(username);
          localStorage.setItem('loggedInUser', username);
          localStorage.setItem('token', token);
          localStorage.setItem('loggedInUser', username);
        }
      }
    } catch (error) {
      console.log('User not logged in:', error);
    }
  };

  // Check for existing user on component mount
  useEffect(() => {
    const user = localStorage.getItem('loggedInUser');
    if (user) {
      setUserData(user);
    } else {
      getUser(); // Fetch user if not in localStorage
    }
  }, []);

  // Logout function to clear user data and navigate to homepage
  const handleLogout = async () => {
    try {
      await axios.get('http://localhost:8080/logout', { withCredentials: true });
      // Clear all relevant localStorage items and reset user data
      localStorage.removeItem('loggedInUser');
      localStorage.removeItem('token');
      setUserData(null);
      navigate('/'); // Redirect to homepage
      window.location.reload(); // Reload to refresh state
    } catch (error) {
      console.log('Error logging out:', error);
    }
  };

  return (
    <Navbar bg="white" expand="lg" fixed="top">
      <Container fluid>
        <Navbar.Brand href="/">Navbar</Navbar.Brand>
        <Navbar.Toggle aria-controls="navbarNavAltMarkup" />
        <Navbar.Collapse id="navbarNavAltMarkup">
          <Nav className="ms-auto">
            <Nav.Link href="#sectionHome" className="active">Home</Nav.Link>
            <Nav.Link href="#sectionKeyFeatures">Key Features</Nav.Link>
            <Nav.Link href="#sectionUploadAndDiagnosis">Upload and Diagnostic</Nav.Link>
            <Nav.Link href="#">Follow Us</Nav.Link>

            {userData ? (
              <NavDropdown title={userData} id="usernameDropdown">
                <LinkContainer to="/profile">
                  <NavDropdown.Item>Profile</NavDropdown.Item>
                </LinkContainer>
                <NavDropdown.Item onClick={handleLogout} className="text-danger">
                  Logout
                </NavDropdown.Item>
              </NavDropdown>
            ) : (
              <Link to="/signup" className="nav-link">Sign Up</Link>
            )}
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
}

export default MyNavbar;
