import React, { useState } from 'react';
import { handleSuccess, handleError } from '../utils.js';
import { Link, useNavigate } from 'react-router-dom';
import { ToastContainer } from 'react-toastify';
import './SignUp.css';

function SignUp() {
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [usernameError, setUsernameError] = useState('');
  const [apiError, setApiError] = useState(''); // State for API error messages
  const navigate = useNavigate();

  // Google Login function
  const loginWithGoogle = () => {
    window.open('http://localhost:8080/auth/google', '_self');
  };

  // Handle form submit for normal signup
  const handleSubmit = async (event) => {
    event.preventDefault();
    const formData = { username, email, password };

    // Simple validation for username
    if (username.trim() === '') {
      setUsernameError('Username is required');
      return;
    } else {
      setUsernameError(''); // Clear error if valid
    }

    try {
      const response = await fetch('http://localhost:8080/auth/signup', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      const data = await response.json();
      const { success, message } = data;

      if (response.ok && success) {
        handleSuccess(message);
        setTimeout(() => {
          navigate('/login');
        }, 2000);
      } else {
        handleError(data.error || 'An unexpected error occurred.');
      }
    } catch (error) {
      handleError('An error occurred. Please try again later.');
    }
  };

  return (
    <div className="signup-page">
      <div className="signup-container">
        <h1 className="sign-up-heading">Get Started</h1>
        <form className="form-container" onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="name" className="signup-label">Username</label>
            <div className="inputForm">
              <svg xmlns="http://www.w3.org/2000/svg" width="20" viewBox="0 0 32 32" height="20">
                <g data-name="Layer 3" id="Layer_3">
                  <path d="M30.853 13.87a15 15 0 1 0-29.729 4.082 15.1 15.1 0 0 0 12.876 12.918 15.6 15.6 0 0 0 2.016.13 14.85 14.85 0 0 0 7.715-2.145 1 1 0 1 0-1.031-1.711 13.007 13.007 0 1 1 5.458-6.529 2.149 2.149 0 0 1-4.158-.759V8.485a1 1 0 0 0-2 0v1.726a8 8 0 1 0 .2 10.325 4.135 4.135 0 0 0 7.83.274 15.2 15.2 0 0 0 .823-7.455zm-14.853 8.13a6 6 0 1 1 6-6 6.006 6.006 0 0 1-6 6z"></path>
                </g>
              </svg>
              <input
                type="text"
                id="name"
                className="signup-input"
                placeholder="Enter your username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                required
              />
            </div>
            {usernameError && <span className="error-text">{usernameError}</span>}
          </div>

          <div className="form-group">
            <label htmlFor="email" className="signup-label">Email Address</label>
            <div className="inputForm">
              <svg xmlns="http://www.w3.org/2000/svg" width="20" viewBox="0 0 32 32" height="20">
                <g data-name="Layer 3" id="Layer_3">
                  <path d="M30.853 13.87a15 15 0 1 0-29.729 4.082 15.1 15.1 0 0 0 12.876 12.918 15.6 15.6 0 0 0 2.016.13 14.85 14.85 0 0 0 7.715-2.145 1 1 0 1 0-1.031-1.711 13.007 13.007 0 1 1 5.458-6.529 2.149 2.149 0 0 1-4.158-.759V8.485a1 1 0 0 0-2 0v1.726a8 8 0 1 0 .2 10.325 4.135 4.135 0 0 0 7.83.274 15.2 15.2 0 0 0 .823-7.455zm-14.853 8.13a6 6 0 1 1 6-6 6.006 6.006 0 0 1-6 6z"></path>
                </g>
              </svg>
              <input
                type="email"
                id="email"
                className="signup-input"
                placeholder="Enter your email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
            </div>
          </div>

          <div className="form-group">
            <label htmlFor="password" className="signup-label">Password</label>
            <div className="inputForm">
              <svg xmlns="http://www.w3.org/2000/svg" width="20" viewBox="-64 0 512 512" height="20">
                <path d="M336 512H48c-26.453125 0-48-21.523438-48-48V240c0-26.476562 21.546875-48 48-48h288c26.453125 0 48 21.523438 48 48v224c0 26.476562-21.546875 48-48 48zm-288-288c-8.8125 0-16 7.167969-16 16v224c0 8.832031 7.1875 16 16 16h288c8.8125 0 16-7.167969 16-16V240c0-8.832031-7.1875-16-16-16zm0 0"></path>
                <path d="M304 224c-8.832031 0-16-7.167969-16-16v-80c0-52.929688-43.070312-96-96-96s-96 43.070312-96 96v80c0 8.832031-7.167969 16-16 16s-16-7.167969-16-16v-80c0-70.59375 57.40625-128 128-128s128 57.40625 128 128v80c0 8.832031-7.167969 16-16 16zm0 0"></path>
              </svg>
              <input
                type="password"
                id="password"
                className="signup-input"
                placeholder="Enter your password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
            </div>
          </div>

          {apiError && <p className="error-text" style={{ color: 'red' }}>{apiError}</p>}

          <button type="submit" className="signup-btn">Sign Up</button>
        </form>
        <ToastContainer />
        <p className="or-text">or</p>
        <button className="social-btn" onClick={loginWithGoogle}>
          <img src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcS6WwgH7Nl5_AW9nDCnR2Ozb_AU3rkIbSJdAg&s" alt="Google" className="social-icon" />
          Google
        </button>
        <div className="text-center">
          <Link to="/login" className="link-button">Have an account? Log in</Link>
        </div>
      </div>
    </div>
  );
}

export default SignUp;
