import React, { useState } from 'react';
import axios from 'axios';
import { useLocation, useNavigate } from 'react-router-dom';
import './OtpVerification.css';

function OTPVerification() {
  const location = useLocation();
  const [otp, setOtp] = useState('');
  const [message, setMessage] = useState('');
  const navigate = useNavigate();
  const { email } = location.state;

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:8080/verify-otp', { email, otp });

      setMessage(response.data.message);
      if (response.data.success) {
        setTimeout(() => {
          navigate('/reset-password', { state: { email } }); // Navigate to Reset Password page
        }, 1000); // 2-second delay to show message before redirect
      }
    } catch (error) {
      setMessage(error.response?.data?.message || 'An error occurred');
    }
  };

  return (
    <div>
      <h2 className = "otp">OTP Verification</h2>
      <form className = "form" onSubmit={handleSubmit}>
        <input
          className = "input"
          type="text"
          value={otp}
          onChange={(e) => setOtp(e.target.value)}
          placeholder="Enter OTP"
          required
        />
        <button type="submit">Verify OTP</button>
      </form>
      {message && <p className='para'>{message}</p>}
    </div>
  );
}

export default OTPVerification;
