import React, { useState } from 'react';
import axios from 'axios';
import { useLocation, useNavigate } from 'react-router-dom'; // To get email from the location state and navigate
import './ResetPassword.css';

const ResetPassword = () => {
    const [newPassword, setNewPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const [message, setMessage] = useState('');
    const [submitted, setSubmitted] = useState(false);

    const location = useLocation(); // Get location state from previous page (OTP verification)
    const { email } = location.state; // Get email from OTPVerification page
    const navigate = useNavigate(); // For redirection after successful password reset

    const handleSubmit = async (e) => {
        e.preventDefault();
        
        // Validate that passwords match
        if (newPassword !== confirmPassword) {
            setMessage("Passwords do not match.");
            return;
        }

        setSubmitted(true);

        try {
            //Send request to reset password with email and new password
            const res = await fetch('http://localhost:8080/reset-password', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ email, newPassword })
            });
            
            const data = await res.json();

            if (res.ok) {
                setMessage("Password has been reset successfully! Redirecting to login...");
                setTimeout(() => navigate('/login'), 2000); // Redirect after 2 seconds
            } else {
                setMessage(data.message || 'Failed to reset password.');
                setSubmitted(false);
            }
        } catch (error) {
            setMessage('Failed to reset password. Please try again.');
            setSubmitted(false);
        }
    };

    return (
        <div className="reset-password-container">
            <form onSubmit={handleSubmit} className="reset-password-form">
                <h2 className='h2'>Reset Password</h2>
                <p>Enter a new password for your account.</p>
                
                {/* Show success or error message */}
                {message && <p className="message">{message}</p>}
                
                <input
                    type="password"
                    placeholder="New Password"
                    value={newPassword}
                    onChange={(e) => setNewPassword(e.target.value)}
                    required
                />
                <input
                    type="password"
                    placeholder="Confirm New Password"
                    value={confirmPassword}
                    onChange={(e) => setConfirmPassword(e.target.value)}
                    required
                />
                
                <button type="submit" disabled={submitted}>
                    {submitted ? 'Resetting...' : 'Reset Password'}
                </button>
            </form>
        </div>
    );
};

export default ResetPassword;
