import React, { useState, useEffect } from 'react';
import { handleSuccess, handleError } from '../utils.js';
import { Link, useNavigate } from 'react-router-dom';
import { ToastContainer } from 'react-toastify';
import axios from 'axios';
import './SignUp.css';

function SignUp() {
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [usernameError, setUsernameError] = useState('');
  const [apiError, setApiError] = useState(''); // State for API error messages
  const navigate = useNavigate();

  // Google Login function
  const loginWithGoogle = () => {
    window.open("http://localhost:8080/auth/google", "_self");
  };

  // Handle form submit for normal signup
  const handleSubmit = async (event) => {
    event.preventDefault();
    const formData = { username, email, password };

    // Simple validation for username
    if (username.trim() === '') {
      setUsernameError('Username is required');
      return;
    } else {
      setUsernameError(''); // Clear error if valid
    }

    try {
      const response = await fetch('http://localhost:8080/auth/signup', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      const data = await response.json();
      console.log(data); // Log response for debugging
      const { success, message, user } = data;

      if (response.ok && success) {
        handleSuccess(message);

        // Redirect to home or dashboard
        setTimeout(() => {
          navigate('/login');
        }, 2000);
      }else {
        if (message) {
          // Set API error message from backend validation errors
          setApiError(message); // Join array of error messages into one string
        } else {
          // Display general error message from backend
          setApiError(message || 'An error occurred. Please try again later.');
        }
      }
    } catch (message) {
      console.error('Error:', message);
      handleError('An error occurred. Please try again later.');
    }
  };
  useEffect(() => {
    // If the user is already logged in (for example after a Google login), redirect them to the home page.
    const token = localStorage.getItem('token');
    if (token) {
      navigate('/'); // Or wherever you want to redirect users when already logged in
    }
  }, [navigate]);
    
  return (
    <div className="signup-page">
      <div className="signup-container">
        <h1 className="sign-up-heading">Get Started</h1>
        <form className="form-container" onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="name" className="signup-label">Username</label>
            <input
              type="text"
              id="name"
              className="signup-input"
              placeholder="Enter your username"
              required
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              autoFocus
            />
            {usernameError && <span className="error-text">{usernameError}</span>}
          </div>
          <div className="form-group">
            <label htmlFor="email" className="signup-label">Email Address</label>
            <input
              type="email"
              id="email"
              className="signup-input"
              placeholder="Enter your email"
              required
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
          </div>
          <div className="form-group">
            <label htmlFor="password" className="signup-label">Password</label>
            <input
              type="password"
              id="password"
              className="signup-input"
              placeholder="Enter your password"
              required
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </div>

          {/* Display the API error message if it exists */}
          {apiError && <p className="error-text" style={{ color: 'red' }}>{apiError}</p>}
          
          <button type="submit" className="signup-btn">Sign Up</button>
        </form>
        <ToastContainer />
        <p className="or-text">or</p>
        <div className="social-buttons">
          <button className="social-btn" style={{ backgroundColor: '#f0f0f0' }} onClick={loginWithGoogle}>
            <img src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcS6WwgH7Nl5_AW9nDCnR2Ozb_AU3rkIbSJdAg&s" alt="Google" className="social-icon" />
              Google
          </button>
        </div>
        <div className="text-center">
          <Link to="/login" className="link-button">Have an account? Log in</Link>
        </div>
      </div>
    </div>
  );
}

export default SignUp;

