import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './ForgotPassword.css';

function ForgotPassword() {
    const [email, setEmail] = useState('');
    const [message, setMessage] = useState('');
    const [error, setError] = useState('');
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();
        setMessage('');
        setError('');

        try {
            const response = await axios.post('http://localhost:8080/forgot-password', { email }, { withCredentials: true });
            
            if (response && response.data) {
                setMessage(response.data.message || 'An email has been sent to reset your password.');
                setTimeout(() => {
                    navigate('/verify-otp', { state: { email } }); // Redirect to OTP verification page with email
                }, 1000); // 2-second delay to show message before redirect
            }
        } catch (err) {
            console.error('Error during password reset:', err);
            if (err.response && err.response.data) {
                setError(err.response.data.message || 'Something went wrong, please try again.');
            } else {
                setError('Network error, please check your connection.');
            }
        }
    };

    return (
        <div className="forgot-password-container">
            <form className="forgot-password-form" onSubmit={handleSubmit}>
                <h2 className="forgot-password-title">Forgot Password</h2>
                <p className="forgot-password-description">Enter your registered email</p>
                <input
                    className="forgot-password-input"
                    type="email"
                    placeholder="Enter your registered email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                />
                <button className="forgot-password-button" type="submit">Send OTP</button>
                {message && <p className="success-message">{message}</p>}
                {error && <p className="error-message">{error}</p>}
            </form>
        </div>
    );
}

export default ForgotPassword;
