import React from "react";
import Api from "./Flask/Api.jsx"
import { useState, useEffect, createContext } from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';
import axios from 'axios';
import SignUp from './Components/SignUp.jsx';
import LogIn from './Components/LogIn.jsx';
import Home from './Components/Home.jsx';
import Profile from './Components/Profile.jsx';
import Error from './Components/Error.jsx';
import UploadAndDiagnosis from './Components/UploadAndDiagnosis.jsx';
import ForgotPassword from './Components/ForgotPassword.jsx';
import ResetPassword from './Components/ResetPassword.jsx';
import OTPVerification from './Components/OTPVerification.jsx';

export const IsLoggedInContext = createContext();
export const SetIsLoggedInContext = createContext();

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [loading, setLoading] = useState(true);

  // This function is passed to LogIn component to update the login state after a successful login
  const handleLoginSuccess = () => {
    setIsLoggedIn(true);
    localStorage.setItem('isLoggedIn', 'true');
  };

  useEffect(() => {
    // Initialize the login state from localStorage when the app first loads
    const storedLoginStatus = localStorage.getItem('isLoggedIn');
    if (storedLoginStatus === 'true') {
      setIsLoggedIn(true);
      setLoading(false);
    } else {
      axios.get('http://localhost:8080/user', { withCredentials: true })
        .then((res) => {
          if (res.data.user) {
            setIsLoggedIn(true);
            localStorage.setItem('isLoggedIn', 'true');
          } else {
            setIsLoggedIn(false);
            localStorage.removeItem('isLoggedIn');
          }
        })
        .catch((err) => {
          setIsLoggedIn(false);
          localStorage.removeItem('isLoggedIn');
          console.log(err);
        })
        .finally(() => setLoading(false));
    }
  }, []);

  // This function handles logging out the user
  const handleLogout = async () => {
    try {
      await axios.get('http://localhost:8080/logout', { withCredentials: true });
      setIsLoggedIn(false);
      localStorage.removeItem('isLoggedIn');
    } catch (err) {
      console.log('Error logging out:', err);
    }
  };

  if (loading) return null; // You could also return a loading spinner here

  return (
    <div>
       <IsLoggedInContext.Provider value={isLoggedIn}>
        <SetIsLoggedInContext.Provider value={setIsLoggedIn}>
          <Routes>
            <Route path="/" element={<Navigate to="/home" />} />
            <Route path="/home" element={<Home />} />
            <Route path="/login" element={isLoggedIn ? <Navigate to="/" /> : <LogIn onLoginSuccess={handleLoginSuccess} />} />
            <Route path="/UploadAndDiagnosis" element={<UploadAndDiagnosis />} />
            <Route path="/upload-and-diagnose" element={<Api />} />
            <Route path="/signup" element={isLoggedIn ? <Navigate to="/" /> : <SignUp />} />
            <Route path="/forgot-password" element={isLoggedIn ? <Navigate to="/" /> : <ForgotPassword />} />
            <Route path="/verify-otp" element={isLoggedIn ? <Navigate to="/" /> : <OTPVerification />} />
            <Route path="/reset-password" element={isLoggedIn ? <Navigate to="/" /> : <ResetPassword />} />
            <Route path="/profile" element={isLoggedIn ? <Profile /> : <Navigate to="/login" />} />
            <Route path="*" element={<Error />} />
          </Routes>
        </SetIsLoggedInContext.Provider>
      </IsLoggedInContext.Provider> 
    </div>
  );
}

export default App;
