import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Profile() {
  const [userInfo, setUserInfo] = useState(null);
  const [diagnosisHistory, setDiagnosisHistory] = useState([]);

  useEffect(() => {
    const fetchUserInfo = () => {
      const userInfoString = localStorage.getItem('loggedInUser');
      if (userInfoString) {
        setUserInfo(userInfoString); // No need to parse it if it's a plain string
      }
    };

    const fetchDiagnosisHistory = async () => {
      try {
        const response = await axios.get('http://localhost:8080/user/diagnosis-history', { withCredentials: true });
        setDiagnosisHistory(response.data.diagnosisHistory);
      } catch (error) {
        console.error('Error fetching diagnosis history:', error);
      }
    };

    fetchUserInfo();
    fetchDiagnosisHistory();
  }, []); // This runs on initial load to fetch diagnosis history

  // Update diagnosis history after a new diagnosis
  

  return (
    <div className="profile-page">
      <h1>Profile Page</h1>
      {userInfo ? (
        <div>
          <p><strong>Username:</strong> {userInfo}</p>
          <h2>Diagnosis History</h2>
          {diagnosisHistory.length > 0 ? (
            diagnosisHistory.map((diagnosis) => (
              <div key={diagnosis._id} style={styles.historyContainer}>
                <div style={styles.imageContainer}>
                  {diagnosis.imageUrl && (
                    <img
                      src={diagnosis.imageUrl}
                      alt={diagnosis.diseaseName}
                      style={styles.image}
                    />
                  )}
                </div>
                <div style={styles.textContainer}>
                  <p><strong>Disease:</strong> {diagnosis.diseaseName}</p>
                  <p><strong>Date:</strong> {formatDate(diagnosis.createdAt)}</p>
                </div>
              </div>
            ))
          ) : (
            <p>No diagnosis history available.</p>
          )}
        </div>
      ) : (
        <p>No user information available. Please log in.</p>
      )}
    </div>
  );
}

// Function to format the date
const formatDate = (createdAt) => {
  const date = new Date(createdAt);
  const dateString = date.toLocaleDateString();
  const timeOptions = { hour: '2-digit', minute: '2-digit', hour12: true };
  const timeString = date.toLocaleTimeString([], timeOptions);
  return `${dateString} at ${timeString}`;
};

const styles = {
  historyContainer: {
    display: 'flex',
    alignItems: 'center',
    marginBottom: '20px',
    borderBottom: '1px solid #ddd',
    paddingBottom: '10px',
  },
  imageContainer: {
    marginRight: '20px',
  },
  image: {
    width: '100px',
    height: '100px',
    objectFit: 'cover',
    borderRadius: '8px',
  },
  textContainer: {
    flex: 1,
  }
};

export default Profile;
