import React, { useState } from 'react';
import axios from 'axios';
import './Api.css';

function Api() {
  const [file, setFile] = useState(null);
  const [result, setResult] = useState(null);
  const [errorMessage, setErrorMessage] = useState('');

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
    setErrorMessage('');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!file) {
      setErrorMessage('Please select an image file before uploading.');
      return;
    }

    const formData = new FormData();
    formData.append('image', file);

    try {
      // Step 1: Send the file to Flask API for prediction
      const predictionResponse = await axios.post('http://127.0.0.1:5000/api/predict', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });
      const predictionData = predictionResponse.data;

      // Step 2: Upload the file to Cloudinary
      const uploadResponse = await axios.post('http://localhost:8080/upload-to-cloudinary', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });
      const { url: imageUrl } = uploadResponse.data;

      // Step 3: Save prediction data and Cloudinary URL in the database
      const token = localStorage.getItem('token');
      if (token) {
        await axios.post('http://localhost:8080/upload-and-diagnose', {
          diseaseName: predictionData.title,
          imageUrl,
        }, {
          headers: { Authorization: `Bearer ${token}` },
        });
      }

      // Step 4: Display results
      setResult({
        ...predictionData,
        image_url: imageUrl, // Include the Cloudinary URL for display
      });
      setErrorMessage('');
    } catch (error) {
      if (error.response?.data?.error) {
        setErrorMessage(error.response.data.error);
      } else {
        setErrorMessage('An unexpected error occurred. Please try again.');
      }
      setResult(null);
    }
  };

  return (
    <div className="main">
      <h1 className="main-heading">Plant Disease Detector</h1>
      <form onSubmit={handleSubmit} className="form">
        <input type="file" className="choose" onChange={handleFileChange} />
        <br />
        <br />
        <button type="submit" className="upload">
          Upload
        </button>
      </form>
      {errorMessage && <p className="error-message">{errorMessage}</p>}
      {result && (
        <div className="container result">
          <h2>Prediction Result</h2>
          <p>Disease: {result.title}</p>
          <img src={result.image_url} alt="Uploaded" />
          <p>Description: {result.description}</p>
          <p>Prevention: {result.prevent}</p>
        </div>
      )}
    </div>
  );
}

export default Api;
