import React, { useState, useEffect } from 'react';
import {useNavigate } from "react-router-dom";
import axios from 'axios';
import './Profile.css';

function Profile() {
  const navigate = useNavigate();
  //const [popup, setPopup] = useState({ visible: false, action: "" });
  const [history, setHistory] = useState([]);
  const [errorMessage, setErrorMessage] = useState('');

  useEffect(() => {
    const token = localStorage.getItem('token');

    if (!token) {
      setErrorMessage('You need to log in to view your profile.');
      return;
    }

    axios
      .post(
        'http://localhost:8080/diagnosis-history',
        { token },
        { withCredentials: true }
      )
      .then((response) => {
        if (response.data.success) {
          setHistory(response.data.history);
        } else {
          setErrorMessage(response.data.message);
        }
      })
      .catch(() => {
        setErrorMessage('Failed to load diagnosis history.');
      });
  }, []);

  // const DeleteAccount = () => {
  //   const [showPopup, setShowPopup] = useState(false);
  
    const handleDeleteAccount = async () => {
      try {
        const token = localStorage.getItem("token"); // Retrieve the user's token
        const response = await fetch("http://localhost:8080/deleteAccount", {
          method: "DELETE",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`,
          },
          body: JSON.stringify({ token: token }),
        });
  
        const data = await response.json();
        if (response.ok) {
          alert(data.message || "Account deleted successfully!");
          localStorage.clear(); // Clear user data
          window.location.href = "/"; // Redirect to homepage
        } else {
          alert(data.message || "Error deleting account.");
        }
      } catch (error) {
        console.error(error);
        alert("An unexpected error occurred.");
      }
    };

    // const handleAction = () => {
    //   if (popup.action === "delete") {
    //     handleDeleteAccount();
    //   } else if (popup.action === "logout") {
    //     handleLogout();
    //   }
    //   setPopup({ visible: false, action: "" });
    // };
  const handleLogout = async () => {
    try {
      await axios.get('http://localhost:8080/logout',{ withCredentials: true });
      // localStorage.removeItem('loggedInUser');
      // localStorage.removeItem('token');
      localStorage.clear();
      navigate('/');
      window.location.reload();
    } catch (error) {
      console.log('Error logging out:', error);
    }
  };

  // return (
  //   <div className="profile">
  //     <div className="profile-header">
  //       <h1 className='h1'>Your Diagnosis History</h1>
  //       <p className='p'>Explore your results with rich visuals and animations.</p>
  //     </div>

  //     {errorMessage && <p className="error-message">{errorMessage}</p>}

  //     <div className="history-container">
  //       {history.length > 0 ? (
  //         history.map((item, index) => (
  //           <div className="history-row" key={index}>
  //             <div className="content">
  //               <h3 className="disease-name">{item.diseaseName}</h3>
  //               <p className="diagnosis-date">
  //                 Diagnosed on: {new Date(item.createdAt).toLocaleDateString()}
  //               </p>
  //               <img
  //                 className="disease-image"
  //                 src={item.imageUrl}
  //                 alt={item.diseaseName}
  //               />
  //             </div>
  //           </div>
  //         ))
  //       ) : (
  //         <p className="no-history">No diagnosis history found.</p>
  //       )}
  //     </div>
  //   </div>
  // );


  return (
    <div className="Profile">
      <div className="card">
        <div className="image"></div>
        <div className="card-info">
          <span>George Johnson</span>
          <p>Support Specialist</p>
        </div>
        <div className="button-card">
          <a href="/" className="button-edit">
            Edit Profile
          </a>
          <a href="/" className="button-delete" onClick={() => setPopup({ visible: true, action: "delete" })}>
            Delete Account
          </a>
        
          <a href="/" className="button-logout" onClick={() => {handleLogout()}}>
            Logout
          </a>
        </div>
      </div>
      <div className="info">
      <div className="info">
  <div className="info-card">
    <h2>Upload History</h2>
    <ul>
      <li>
        <span>Document 1</span>
        <span className="upload-date">2024-12-01</span>
      </li>
      <li>
        <span>Image 2</span>
        <span className="upload-date">2024-12-01</span>
      </li>
      <li>
        <span>PDF Report</span>
        <span className="upload-date">2024-11-30</span>
      </li>
    </ul>
  </div>
  </div>
      </div>
      {popup.visible && (
        <div className="popup-overlay">
          <div className="popup">
            <h3>
              {popup.action === "delete"
                ? "Are you sure you want to delete your account?"
                : "Are you sure you want to logout?"}
            </h3>
            <p>
              {popup.action === "delete"
                ? "This action is irreversible."
                : "You will need to log in again to access your account."}
            </p>
            <div className="popup-buttons">
              <button onClick={handleAction} className="confirm-btn">
                Yes
              </button>
              <button
                onClick={() => setPopup({ visible: false, action: "" })}
                className="cancel-btn"
              >
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
  
}

export default Profile;
